mapper.update:
UPDATE avg_score
SET score = #{score}
WHERE id=#{id}
AND content_code=#{contentCode}

mapper.select:
select content_code as contentCode,  score
from avg_score
where content_code = #{contentCode}
and id = #{id}

mapper.update:
update avg_score
set wish_list = #{wishList}
where
id = #{id}
and 
content_code =  #{contentCode}

mapper.select:
select content_code as contentCode, wish_list from avg_score where
content_code = #{contentCode} AND
id = #{id}
