mapper.select:
SELECT * FROM( SELECT T1.*,COUNT(*) OVER() AS CNT FROM( SELECT * FROM TRAVELGE_INFO
TRAVELGE_REGION LIKE '%'||#{travelgeRegion}||'%' or TRAVELGE_THEME LIKE '%'||#{travelgeTheme}||'%' or CONTENT_CODE LIKE '%'||#{contentCode}||'%' or TRAVELGE_ADDR LIKE '%'||#{travelgeAddr}||'%'
CONTENT_CODE LIKE '%'||#{contentCode}||'%' TRAVELGE_NAME LIKE '%'||#{travelgeName}||'%' TRAVELGE_ADDR LIKE '%'||#{travelgeAddr}||'%' TRAVELGE_REGION LIKE '%'||#{travelgeRegion}||'%'
TRAVELGE_THEME = #{travelgeTheme} and TRAVELGE_REGION =#{travelgeRegion}
1=1
) T1) order by content_code

mapper.update:
UPDATE TRAVELGE_INFO
SET TRAVELGE_NAME = #{travelgeName}, TRAVELGE_ADDR = #{travelgeAddr},
TRAVELGE_DESCRIPTION = #{travelgeDescription},
TRAVELGE_THEME = #{travelgeTheme}, TRAVELGE_REGION = #{travelgeRegion},
TRAVELGE_COORDINATES = #{travelgeCoordinates}
,TRAVELGE_PHOTOS = #{travelgePhotos}
WHERE CONTENT_CODE = #{contentCode}
